INSERT INTO `dt_vistava` (`id_rep`, `id`, `tip`, `photozag`, `nazva`, `avtor`, `times`, `opis`, `start`, `end`, `type`) VALUES
(1, 44, 'хроніка життя', '10', 'Тисяча снопів вітру', 'Іван Миколайчук', 'тривалість - 1 год. 30 хв. без антракту', '34 ролі в кіно, 9 сценаріїв, та 2 режисерські роботи. Його називали обличчям і душею українського поетичного кіно, аристократом духу, блискучим самородком.\n\nВ його особі українська нація має світового невмирущого позитивного героя, який пробуджував національний дух українців. Проте звання народного артиста Івану Миколайчуку так і не присвоїли, бо тодішні ідеологи винесли акторові вирок - "націоналіст". Державну Шевченківську премію Іван Миколайчук отримав вже посмертно.\n\nКажуть, того року, коли він помер, на стави його рідного села Чорторий прилетіли лебеді. Люди назвали їх Івановими…\n\nВистава для тих, хто хоче більше дізнатися про Івана Миколайчука, про події, які вплинули на його долю. Це не просто біографічна постановка, а яскрава історія життя, у якій поєднано колоритний буковинський гумор, пісні, танці з реаліями тогочасного суспільства.\n\nУ виставі багато символіки, і найважливіше те, у ній постануть друзі, колеги Івана Миколайчука і сама Марічка Миколайчук.\n\nРоль молодого Миколайчука грає Григорій Руденко-Краєвський, Івана у зрілому віці втілить актор Національного драматичного театру ім. І. Франка Анатолій Чумаченко (м. Київ). У виставі використано народні пісні та пісні у виконанні Марічки Миколайчук.\n\nРежисер та співавтор сценарію – засл. арт. України Людмила Скрипка', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 49, 'драма', '15', 'Юліан Цезаревич', 'за романом "Апостол черні" О. Кобилянська', 'тривалість - 2 год.', 'Роман «Апостол черні» – останній великий твір Ольги Кобилянської, у радянські часи був заборонений як націоналістичний і не видавався. Його проблематика, тема апостольського служіння рідному народові, українська національна ідея – все це відлякувало тодішніх укладачів видань творів письменниці. Сьогодні Чернівецький академічний музично-драматичний театр імені Ольги Кобилянської вдруге звертається до цього унікального інтелектуального твору і прагне з театральної сцени говорити про як ніколи актуальні нині проблеми.\nрежисер-постановник, автор сценічної версії та музичного оформлення – засл. діяч мистецтв України Мирослав Гринишин\n\nхудожник-постановник – Андрій Романченко\n\nбалетмейстер-постановник – Василь Гринюк\n\n«…З жестом на будуче, де не лише сам націоналізм гратиме ролю в житті і культурному розвитку України, але де нарід повинен іти дальше, здобувати Європу, цебто цивілізацію в своїй хаті, в своїй душі, творити свою власну українську культуру'' Нам треба характерів, робітників завзятих, що беруться і за найдрібнішу цеглу, не погорджуючи нею для будовання своєї держави''…» – про «Апостола черні» з листа Ольги Кобилянської до Степана Смаль-Стоцького, 1927 рік.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 77, 'Казка', '5', 'Снігова Королева', 'Є. Шварц', 'Тривалість - 1 год. 20 хв.', 'Незвичайні пригоди Герди та Кея захоплять не лише маленьких глядачів, а й їх батьків, і поведуть у таємничий світ \n</p><p>\nказки Ганса-Хрістіана Андерсена.\nРежисер-постановник вистави - В''ячеслав Жила.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 78, 'Казка', '2', 'День народження кота Леопольда', 'За А. Хайтом', 'тривалість - 1год. 20хв.', 'У звичайному житті коти полюють на мишей, а в казці Аркадія Хайта “День народження кота Леопольда” усе відбувається навпаки. Лагідний і доброзичливий кіт Леопольд раз у раз потерпає через зухвалих і набридливих мишей.\n</p><p>\nНавіть у день народження Леопольда не обійшлося без прикрощів. Своїми поганими жартами мишенята довели іменинника до відчаю. На які лише хитрощі вони не вдавалися: викрадали лікарську валізу, прикидалися працівниками санепідемстанції, мамою-свинею з дитям-поросятком, і навіть конячкою – однак нічого з того не вишло. Миші самі ж за все й поплатилися. Але Леопольд разом зі своїми друзями пробачив мишам і запропонував їм подружитися. Миші пообіцяли ніколи більше не ображати Леопольда і своєї обіцянки дотримали.\n</p><p>\nМузична казочка принесе масу задоволення, як дорослим, так і дітям.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 79, 'Дитяча вистава', '3', 'Жила собі Сироїжка', 'В. Зимін', 'Тривалість - 1 год.', 'Цікава історія з життя лісових грибочків, які теж можуть розмовляти, сміятись і навіть плакати. Вони, як і люди, – бувають добрі і погані, у кожного свій характер, свої примхи. І от одного разу погані гриби викрадають чарівну скриню, яка дарує лісу дощ. Хто ж врятує ліс від засухи, хто наважиться на поєдинок зі злим Мухомором? Ви дізнаєтесь це, якщо завітаєте до казкового світу театру і побачите незвичайні пригоди Сироїжки та її друзів.\n</p><p>\nРежисер – Володимир Грицак.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 80, 'Музична казка', '4', 'Карлсон, який живе на даху', 'За А. Ліндгрен', 'Тривалість - 1 год. 20 хв.', 'Карлсон - добре відомий для глядачів з радянських фільму та мультика. На сцені цього кумедного персонажа, принаймні, у Чернівцях ще ніхто не бачив. Це наш Карлсон, якого не було ні в Радянському Союзі, ні в американців, ні в сучасних мультфільмах українських. Проте, характерні риси Карлсона все ж збережені: він веселий, непосидючий, багато бешкетує і, звичайно ж, як і в мультфільмі, літає.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 83, 'Музична казка', '1', 'Як Настуня ледь не стала марою', 'В. Ілюхов', 'Тривалість - 1год. 20хв.', 'Повчальна історія про нечепуру Настуню, яка сподобалась підступній Бабі Язі і та вирішила викрасти дівчинку, щоб зробити з неї мару. Аби повернути Настуню, в дорогу вирушають сестричка Оленка і Домовичок. Яким чином вдасться визволити Настуню з хатинки Баби Яги – про це Ви зможете дізнатися самі, відвідавши виставу. Яскрава музична вистава неодмінно сподобається Вам та Вашим дітям. \n</p><p>\nРежисер – Андрій Романов.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 84, 'Музична казка', '6', 'Кішчин дім', 'С. Маршак', 'Тривалість - 1 год. 10 хв.', 'У будинку заможної й гордовитої Кицьки прийом поважних гостей: Півня з Куркою, Цапа з Козою та веселої Свині. Лише бідолашним голодним небожам – кошенятам відмовлено у гостинності. Та коли трапляється пожежа, і чудовий будинок перетворюється на попіл, стає зрозуміло, хто справжній друг і помічник у біді. \n</p><p>\nАвтори вистави створили яскраву музичну історію за мотивами казки для найменших глядачів.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 85, 'Музична казка', '30', 'Білосніжка і семеро гномів', 'За мотивами казки братів Грімм', 'Тривалість – 1 год.', 'Всесвітньо відома історія Білосніжки та гномів поведе у світ казки. І казка ця не тільки для дітей, але й для дорослих, адже основна тема, на якій наголошує режисер Андрій Романов – це дружба. У дружбі і єдності сила гномів, тільки разом вони здатні перемагати зло.\n</p><p>\nДіти бачать у казці – одне, дорослі – зовсім інше, та одне буде спільним – гарний настрій і незабутні враження.\n</p><p>\nрежисер-постановник – Андрій Романов.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 86, 'Дитячий мюзикл', '7', 'Ще раз про червону шапочку', 'Ю. Кім', 'Тривалість - 1 год. 15 хв.', 'Бабуся запросила Червону Шапочку на пиріжки, та дорога довга, через ліс, а там вовк… І не лише він… На шляху дівчинки трапляється Баба Яга, Айболит з Бармалієм, Снігова Королева та Іван Царевич. Добра і повчальна казка про те, що поганих – не буває, неодмінно сподобається не лише діткам, але й дорослим. \n</p><p>\nРежисер - Андрій Романов.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 91, 'Комедія на дві дії', '2', 'Кайдашева сім''я', 'За І. Нечуй-Левицький', 'Тривалість - 2год. 10хв.', 'Відомий твір класика української літератури Івана Нечуя-Левицького й досі не втратив своєї актуальності. Родинні суперечки у боротьбі за власність і вищість над іншими, викликають сміх крізь сльози.\n</p><p>\nСоціально-побутовий жанр оповідання у новій виставі набуває трагікомедійного звучання. Старий Кайдаш намагається відшукати сенс буття дорогою з церкви до шинка. Просто на очах перетворюються характери Мелашки та Лавріна. Дрібні побутові суперечки переростають у безглузду війну рідних людей. Хто зупинить шалене коло ворожнечі? Чи врешті настане між Кайдашенками мир? Про це дізнаються глядачі, подивившись виставу.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 92, 'Комедія на дві дії', '1', 'Різдвяна ніч', 'За М. Гоголем', 'Тривалість - 2 год.', 'Останній день перед Різдвом минув. Зимова ясна ніч настала. Глянули зірки. Все голосніше й голосніше лунали вулицями пісні і вигуки. Парубки жартували досхочу. Здавалося, до ранку готові веселитися. І ніч, як на те, так розкішно теплилась. Місяць велично виплив на небо сяяти добрим людям ісвіту, щоб було весело колядувати, славити Христа, бажати здоров''я господарю, господині, дітям, усій родині... \n</p><p>\nІнсценізація та постановка нар. арт. України Олега Мосійчука.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 99, 'Драма на три життя', '3', 'Солодка Даруся ', 'За М. Матіос', 'Тривалість - 2год. 15хв.', 'Саме в Чернівцях, у березні 2008 року відбулася перша постановка твору лауреата Шевченківської премії Марії Матіос «Солодка Даруся». Прем’єра з великим успіхом пройшла в Чернівцях. З виставою театр побував на фестивалі «Сцена людства - 2008» (м. Черкаси), двічі на гастролях у Львові, а також у Києві, Рівному, Чернігові, Луцьку, де отримувала шквал оплесків та чимало схвальних відгуків. \n</p><p>\nДрама на три життя – саме так визначили жанр вистави. Адже події, які привели Дарусю до її трагедії, вплинули не тільки на неї. Це історія про покалічені душі багатьох людей. Дія відбувається на Буковині, середина XX століття. Маленька Даруся за цукерку розповідає радянському офіцеру про те, що батько допоміг упівцям. Ця цукерка стає для неї фатальною. Крізь усе життя Даруся пронесе свій хрест... \n</p><p>\nУ виставі задіяний майже увесь творчий склад. \n</p><p>\nРежисер і автор інсценізації - засл. арт. України Людмила Скрипка', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 100, 'Лірична комедія', '8', 'Тустеп і різний "мотлох"', 'Р. Байєр', 'Тривалість - 1 год. 30 хв.', 'Крістін Мільман – «розкішна жінка, з головою на плечах і без претензій». Герман Льюїс - «чоловік, який всього досяг самотужки, своїм гострим розумом може «дістати» кого завгодно, та може і... зачарувати».\n</p><p>\nТак автор охарактеризував героїв п’єси.\n</p><p>\nКрістін і Герман давно дружили сім''ями, та обоє втратили свої «половинки». Крістін відзначила роковини смерті чоловіка і вирішила покинути рідне місто. Герман намагається переконати її залишитися. Діалог, який триває протягом усієї вистави, нагадує блискучу джазову імпровізацію. Дотепні, неординарні немолоді люди говорять про життя, смерть і кохання. Цей фантастичний дует переростає у сварки, у зізнання, у танець...\n</p><p>\nУ виставі можна буде почути живий спів під супровід оркестру.\n</p><p>\nРежисер-постановник – засл. арт. України Людмила Скрипка\n</p><p>\nХудожник-постановник – Наталія Руденко-Краєвська', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 101, 'Фрашка (жарт)', '7', 'Як козам роги виправляють, або приборкання норовливої', 'Юрій Федькович & Вільям Шекспір', 'Тривалість - 1 год. 30 хв.', 'Ю. Федькович умів сміятися і відобразив цей колоритний буковинський гумор у своїх п’єсах. Одна з кращих його комедій «Як козам роги випраляють», що була написана на основі шекспірівської п’єси «Приборкання норовливої» вперше постає на сцені Чернівецького акдемічного муздрамтеатру ім. О.Кобилянської.\n</p><p>\n«Вільно за Шекспіровою драмою» – таку характеристику дав Ю. Федькович своїй фрашці «Як козам роги виправляють».Творці вистави вдало поєднали дві абсолютно різні культури – англійську та українську, створивши на сцені абсолютно нові креативні образи та умови існування для акторів. У п’єсі піднято вічне питання боротьби чоловічого і жіночого. Ю. Федькович показав сутичку двох стихій – води і вогню, чорного та білого, вкотре, підтвердивши, що всеперемагаюче кохання може створити гармонію і перетворити на покірну кішечку навіть хижого тигра.\n</p><p>\nПопередження: ті, хто сподівається побачити традиційний водевіль Ю. Федьковича (за В. Шекспіром), будуть шоковані. \nРежисер-постановник – відомий київський режисер, заслужений діяч мистецтв України Сергій Архипчук. Художник-постановник – Ніна Мурашкіна. Музика – Богдан Сегін та Олена Сєрова.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 102, 'Мелодрама', '9', 'Ілюзії вальсу', 'Автор сценічної версії Людмила Скрипка', 'Тривалість - 1 год. 40 хв.', 'Історія про трьох різних дівчат зі своїми абсолютно різними прагненнями та уявленнями про життя. У кожної з них є своя мрія. Одна мріє стати художницею, інша – великою піаністкою, а третя хоче звичайного жіночого щастя – вийти заміж і народити дітей.\n</p><p>\nНа шляху до втілення мрій, у однієї з дівчат (піаністки) виникають ситуації, які заважають її здійсненню, і, зрештою, призводять до трагедії…\n</p><p>\nГлядачі вперше побачать сценічне поєднання творів Ольги Кобилянської: «Меланхолійного вальсу» та «За ситуаціями», у яких можна прочитати долю самої письменниці.\n</p><p>\n«Прочили-сте «Valse melancolique» і знаєте історію мого життя. Се моя історія. Більше не кажу нічого. Тепер мені так часто zu Mute [на душі], що я хтіла тріснути, як та струна, але знаю, що не трісну».\nЗ листа Ольги Кобилянської до \nОсипа Маковея від 17 лютого 1898 року\n</p><p>\n«За ситуаціями» я написала під вражінням гри одної великої драматичної акторки, котра тепер виступає, коли ще жиє, лише в кіно, а то Асти Нільсен. Праця мала бути їй присвячена. […] В тій праці стільки гарних, безсловесних, мною пережитих хвилин! У ту новелу вложила я послідні лілії своєї душі і молодих іще почувань. Характер героїні, трохи несамовитий і, як німці кажуть, «bizarr» [химерний] – я окреслила його до такого ступеня, до якого мусив би був розвинутися. Писала я ту новелу з такою силою чуття, що, викінчуючи її, я цілий вечір плакала». \nЗ автобіографії «Про себе саму» \nу листах до професора Степана Смаль-Стоцького, 1921 р. \n</p><p>\nРежисер і автор сценічної версії – засл. арт. України Людмила Скрипка.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 103, 'Музична комедія на дві дії', '12', 'Шельменко-Денщик', 'Г. Квітка-Основ', 'Тривалість - 2 год. 10 хв.', 'Запрошуємо побачити улюблену виставу українців – комедію «Шельменко-денщик» за Г. Квіткою-Основ’яненком. Автор настільки вдало описав характери героїв, які яскраво відображають нашу ментальність, особливо, вміння виживати за найрізноманітніших обставин, що ця вистава не покидає репертуарні афіші багатьох українських театрів. П’єса неодноразово ставилася і в нашому театрі (1944, 1991 рр.) Це була одна з улюблених п’єс Василя Василька, легендарний режисер навіть сам грав Шельменка. \n</p><p>\nХитромудрі інтриги, що її плете бувалий Шельменко, прагнучи з`єднати закохану пару. А поруч наївно-комічні батьки – патріархальні поміщики Шпаки і, ясна річ, жених, що безнадійно залицяється. \n</p><p>\nУсе це, помножене на акторський кураж та багатобарвність театральних засобів, породжує веселе, дотепне дійство, проте, не без моралі.\n</p><p>\nРежисер-постановник – заслужений діяч мистецтв України Віталій Денисенко\n</p><p>\nУ ролі Шельменка – відомий за багатьма виставами актор Микола Гоменюк. У виставі також можна побачити заслужених артистів України: Богдана Братка, Ларису Попенко, Катерину Чумакову, а також молодих артистів театру: Анну Моргунову, Олексія Надкерничного та інших.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 104, 'Більше ніж комедія', '13', 'Інцидент', 'Л. Лунарі', 'Тривалість - 2 год. 10 хв.', 'Під час відкриття нового відділення банку стався прикрий інцидент, який викликав неабиякий резонанс та інтерес, безліч пікантних ситуацій і багато сміху.... Що ж за інцидент трапився у театрі? Приходьте і дізнаєтесь самі. Ефектна, весела та видовищна вистава за п’єсою італійського драматурга Луїджі Лунарі подарує Вам хороший настрій та незабутні враження. \n</p><p>\nРежисер – заслужена артистка України Людмила Скрипка.\n</p><p>\nХудожник-постановник – Наталія Руденко-Краєвська.\n</p><p>\nБалетмейстер – Неля Зберя.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 105, 'Комедія на дві дії', '29', 'Кохання у стилі бароко', 'Андрій Романов', 'Тривалість – 1 год. 40 хв.', 'Дія відбувається у маєтку графа-бешкетника‚ витівника‚ здатного на всілякі вибрики та жарти. Його парубоцька вечірка захлинається "страшною" звісткою: їде наречена! В плани затятого холостяка одруження аж ніяк не входить. Проте існує родинний заповіт: якщо не одружиться на Оляні, з якою покійні батьки заручили його десять років тому, він позбавляється спадщини. Єдиний вихід і гроші зберегти‚ і волю парубоцьку – примусити наречену саму зректися шлюбу. Отже, граймося, хлопці! Здається, ідея шикарна: граф перевбирається на свого псаря Онисима, а той, нічого не розуміючи через велику пиятику, стає "графом". Але не так сталося, як гадалося…\n</p><p>\nНестримний потік жартів, чудові віршовані монологи. Чергова історія кохання, але у вишуканому обрамленні у стилі бароко.\n</p><p>\nрежисер – Андрій Романов\nхудожник – Олена Зотова', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 106, 'Музична комедія', '27', 'Малюк', 'Жан де Летраз', 'Тривалість – 2 год.', 'Дія відбувається у Парижі, у домі родини Фонтанж. У зовсім юного Жака (синочка власника велосипедної фабрики) з''явився малюк, який зовсім не входить у його плани. Як приховати від батьків появу малюка? У юного ловеласа голова йде обертом. І тоді на допомогу приходить рідна сестра. Вона повідомляє батькам, що це її малюк! Все перемішалося у домі Фонтанж! Малюка ховають: то малюк нікому не потрібен, а то раптом потрібен усім! Справжній водевіль!\n</p><p>\nЯскрава, видовищна музична сучасна вистава про кохання, з безліччю кумедних ситуацій допоможе Вам відволіктися від буденності.\n</p><p>\nРежисер – засл. діяч мистецтв України Віталій Денисенко\nХудожник-постановник – Наталія Тарасенко \n</p><p>\nза мотивами казки Сергія Козлова', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 107, 'Вестерн по-чернівецьки', '31', 'Срібний павук', 'За В. Кожелянком', 'Тривалість – 2 год.', '"Срібний павук" – це унікальна вистава про таємниче, романтичне і трохи бандитське місто Чернівці минулого століття за однойменним романом Василя Кожелянка.\n</p><p>\nУ виставі іронічно поєднано найдрібніші подробиці життя передвоєнних Чернівців 1939-го року і придумані історії, що служать лаштунками для пригодницької інтриги. Містифікація, іронія, побутові подробиці, романтичні і гумористичні діалоги – все це на історичному тлі.\n</p><p>\nВистава також прикрашена своєрідним гумором, соковитою буковинською говіркою та перепитіями любовного трикутника.\n</p><p>\nЗа сюжетом, велику частину часу чернівецькі детективи Кароль та Гельмут проводять у ресторанах, ганяються за бандитами або... у душевних муках від нерозділеного кохання. Хоча саме ця безнадійна закоханість у красуню Марію дарує їм різні пригоди... \n</p><p>\nУ виставі зайнятий увесь акторський склад. \n</p><p>\nРежисер – нар. арт. України Людмила Скрипка.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 108, 'Драма на дві дії', '17', 'Земля', 'За О. Кобилянською', 'Тривалість - 3 год.', 'Театр представляє нову постановку легендарної повісті Ольги Кобилянської. На відміну двох попередніх, основу тексту нинішньої інсценізації становить рукопис Василя Василька (1946 р.), адаптований Мирославом Гринишиним, із залученням другої частини.\n</p><p>\nГлядачі мають змогу побачити іншу «Землю» – історію Сави, людини, котра пішла на гріх заради землі. У виставі піднята не тільки тема влади землі над людиною, а насамперед, психологія людської душі. Не зважаючи на те, що трагедія братовбивства – реальний факт, драматургія розгортається через символічний та в певній мірі містичний показ внутрішнього світу героїв, якими обгорнуті події.\n</p><p>\nІсторія Сави – людини, яку перемогла земля, не втрачає актуальності й сьогодні, не зважаючи на те, що була написана сто літ тому. Скільки нам ще потрібно століть, щоб усвідомити, що людське життя і скалічені долі – це надто висока плата за землю?..\n</p><p>\nЛегендарний твір, вражаюча вистава варті того, щоб це побачив кожен український глядач.\n</p><p>\nУ виставі задіяний увесь акторський склад, балет та оркестр Чернівецького академічного музично-драматичного театру ім. О. Кобилянської. \n</p><p>\nРежисер-постановник – засл. діяч мистецтв України Мирослав Гринишин\n</p><p>\nХудожник-постановник – Андрій Романченко\n</p><p>\nКомпозитор, диригент – Володимир Шнайдер', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 109, 'Музична кумедія-лубок', '16', 'Сватання на Гончарівці', 'Г. Квітка-Основ`яненко', 'Тривалість - 2 год.', 'Це одна з улюблених класичних комедій українців. Напевно нема жодного драматичного театру, який б не мав у своєму репертуарі цієї чудової музичної комедії. В історії Чернівецького театру вистава «Сватання на Гончарівці» також була поставлена кілька разів (у 1944, 1959 та 1973р).\n</p><p>\nУ 2015 р. ми пропонуємо її у постановці режисера – засл. діяч мистецтв України Віталія Денисенка, який вирішив спробувати розшифрувати одвічний код українського національного гумору за допомогою лубкового мистецтва (лубок – народна картинка, що містить деякий повчальний і духовно-моральний сюжет, відрізняється дохідливістю образу і призначена для масового поширення). Тож запрошуємо подивитися таку собі кумедію-лубок, або ж виставу-комікс про славнозвісне сватання Уляни, Олексія та Стецька.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 110, 'Вистава для усієї родини', '18', 'Енні', 'за мотивами однойменного фільму', 'Тривалість - 1 год. 40 хв.', 'Всесвітньо відома історія про маленьку дівчинку-сироту Енні вперше на українській сцені.\n</p><p>\nЕнні разом з іншими сиротами росте у дитячому притулку, у неї є мрія – мати справжніх маму і тата. Попри те, що батьки дівчинки загинули у автокатастрофі, вона щиро вірить у те, що вони живі і незабаром прийдуть по неї. Життя у сиротинці геть «не солодке», вихователька міс Феніган всіляко знущається над дітьми. Незважаючи ні на що, Енні вірить у диво, і воно стається… на Різдво.\n</p><p>\nВистава про родинне тепло, якого, на жаль, багатьом у цьому світі так бракує.\n</p><p>\nТож запрошуємо усією родиною подивитися виставу!\n</p><p>\nВистава успішно була представлена на декількох фестивалях, зокрема, на фестивалі «Коломийські представлення-2015» була високо відзначена головою журі Адою Роговцевою.\n</p><p>\nРежисер вистави – художній керівник театру, засл. арт. України Людмила Скрипка.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 111, 'Вистава без антракту', '19', 'Маестро і Кароліна', 'Історія великого кохання Ференца Ліста, що зародилось в Україні', 'Тривалість - 1 год. 45 хв.', 'Це перша театральна постановка про легендарне кохання геніального угорського композитора Ференца Ліста і княгині Кароліни Сайн-Вітгенштейн, що зародилося в Україні і надихнуло маестро на створення справжніх шедеврів.\n</p><p>\nОдин з найвідоміших його творів – «Угорська рапсодія №2» (з серії 19 угорських рапсодій) – написаний в Україні. \n</p><p>\nКароліна познайомила маестро з українськими народними піснями, а він присвятив їй свої кращі симфонічні поеми.\n</p><p>\nПостать композитора оповита легендами і суперечливими фактами. Кажуть,  що на концерт Ференца Ліста у Києві 1847 року Кароліна викупила майже всі квитки. \nЧернівецький театр ім. О.Кобилянської привідкриває завісу життя Маестро і Кароліни. \nАвтор сценарію і режисер-постановник – нар. арт. України Людмила Скрипка\n</p><p>\nхудожник-постановник – Наталія Тарасенко \nкомпозитор, аранжувальник – Володимир Шнайдер \nхормейстер  – Оксана Первова-Рошка\nбалетмейстер – Мирослава Воротняк', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(1, 112, 'Комедія', '21', 'Запечатаний Двірник', 'Юрій Федькович', 'Тривалість - 1 год. 45 хв.', 'Фрашка (комедія) «Запечатаний двірник» Юрія Федьковича – це своєрідна візитна картка буковинського гумору. П’єса написана ще в кінці 19 століття, але не втрачає актуальності й сьогодні. За нехитрою жартівливою історією стоїть не тільки й не стільки кохання, залицяння та ревнощі... У житті невеличкого містечка можна побачити багато цікавого, з проекцією на сьогодення. Подивіться, за всієї безглуздості ситуації із посадовою особою, запечатаною у конфіскованій шафі, ніхто не наважується порушити цю печатку, навіть сам «запечатаний» Двірник – повага ж бо до закону!\n</p><p>\nВистава вперше була поставлена у 1984 році режисером, народним артистом України Анатолієм Литвинчуком до відзначення 150-річчя буковинського кобзаря. Згодом, у 2004 році її відновив засл. арт. України Омелян Савка.\n</p><p>\nСьогодні театр пропонує нову постановку цієї блискучої комедії – інші актори, інший режисер, художник, незмінна тільки мова буковинського соловія Юрія Федьковича.\n</p><p>\nрежисер-постановник – засл. діяч мистецтв України Мирослав Гринишин \nхудожник-постановник – Андрій Романченко  \nкопмозитор – Василь Городенський\nавтор аранжування, диригент – Володимир Шнайдер\nхормейстер – Оксана Первова-Рошка\nбалетмейстер-постановник – Василь Гринюк\nхудожник освітлення – Володимир Калінін\nзвукорежисер – Наталія Богач \nзавідувач художньо-постановочною частиною – Василь Губко\nпомічник режисера – Юлія Рижкова', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 113, 'Казка', '28', 'Пригоди морського вовка', 'За С. Козловим', 'Тривалість – 1 год.', 'Будь-які перешкоди, труднощі, непорозуміння подолають ті, хто вміє мріяти, любити, дружити. Адже якщо поруч вірний друг, можна подолати все. І навіть якщо трапляться труднощі, то справжня дружба ними лише загартується. Вистава «Пригоди морського вовка» саме про це. Вона буде цікава в першу чергу для дітей молодшого віку, але й будь-який дорослий отримає величезне задоволення, поринувши у світ мрій та фантазій.\n</p><p>\nРежисер-постановник – нар. арт. України Людмила Скрипка.\n</p><p>\nЗа мотивами однойменного роману Василя Кожелянка.', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 117, 'Музична казка', '32', 'Золоте курча', 'В. Орлов', 'Тривалість вистави – 1 год.', 'Музичну казку «Золоте курча» ми створили спеціально для найменших глядачів, які лише починають долучатися до театрального мистецтва.\n</p><p>\nЦе добра й проста історія про довірливе маленьке Курча, якого хоче використовувати хитра Лисиця, перетворивши його в квочку, яка несе золоті яйця. Але підступним планам злої хижачки не судилося здійснитися, адже у маленького курча є захисник – його люблячий «татусь» – найдобріший, найрозумніший, найсильніший і зовсім не страшний сірий Вовк!\n<br><br>\nрежисер-постановник – Артем Лещик<br>\nхудожник – засл. працівник культури України Петро Магратій<br>', '0000-00-00 00:00:00', '0000-00-00 00:00:00', ''),
(2, 118, 'Весела історія', '33', 'Різдвяна пригода мишенят', 'За мотивами казок Беатрікс Поттер', 'Тривалість вистави – 1 год.', 'У невеличкому будинку, де крім людей є ще й кішка, раптом завелися миші. Що поробиш, у добрі і теплі хочеться жити усім. Миші не розуміють, чому їм не можна жити разом з людьми – це ж так весело! Але як же це пояснити людям?\n</p><p>\nЧерез веселу історію напередодні Різдва у житті мишей глядачі можливо впізнають свої життєві історії і побачать у маленьких мишках людські характери. Бо у нашій казці миші, як і люди, вміють дружити, сваритись, боятись, ображатись і навіть… любити.  Особливого шарму цій історії додає святковий дух Різдва.\n</p><p>\nУ ролях:<br><br>\nзасл. арт. України Лариса Попенко,<br>\nМарічка Романова,<br>\nОльга Кільчицька-Дудка,<br>\nАнна Забяло,<br>\nАндрій Топорівський,<br>\nВадим Глазнєв. \n</p><p>\n У виставі беруть участь вихованці театральної студії для дітей та молоді.<br><br>\nрежисер-постановник – нар. арт. України Людмила Скрипка,<br>\nхудожник-постановник – Наталія Тарасенко,<br>\nмелодії до пісень – Оксана Первова-Рошка,<br>\nвірші до пісень – Софія Кримовська,<br>\nбалетмейстери-постановники – Василь Гринюк, Марія Циба\n', '0000-00-00 00:00:00', '0000-00-00 00:00:00', '');
